import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";

import establishments from "./../components/establishments.json";

import Hotels from "./../components/hotels";

function Home() {
  useEffect(() => {}, []);

  let handleHotelSearch = (input) => {
    const hotelSearch = input.target.value;
  };

  return (
    <>
      <div className="container">
        <div className="searchField">
          <div>
            <input
              type="text"
              placeholder={`Search in all cards`}
              autoFocus
              onChange={handleHotelSearch}
            />
          </div>
        </div>
      </div>
      <div className="container overview">
        {!establishments ? (
          establishments.map((value, index) => {
            return (
              <div key={index}>
                <Hotels
                  key={index}
                  name={value.establishmentName}
                  price={value.price}
                />
              </div>
            );
          })
        ) : (
          <>
            <ReactLoading
              type={"spinningBubbles"}
              color={"#51a9b6a1"}
              height={100}
              width={100}
            />
          </>
        )}
      </div>
    </>
  );
}

export default Home;
