import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";

import establishments from "./../components/establishments.json";

import Hotels from "./../components/hotels";

function Home() {
  const [debounce, setDebounce] = useState();
  useEffect(() => {}, []);

  let handleHotelSearch = (input) => {
    if (debounce) {
      clearTimeout(debounce);
    }
    const hotelSearch = input.target.value;
    setDebounce(
      setTimeout(() => establishments(hotelSearch)),
      300
    );
  };

  return (
    <>
      <div className="wideCardContainer">
        <div className="searchField">
          <div>
            <input
              type="text"
              placeholder={`Search in all cards`}
              autoFocus
              onChange={handleHotelSearch}
            />
          </div>
        </div>
      </div>
      <div className="smallCardContainer overview">
        {establishments ? (
          establishments.map((value, index) => {
            return (
              <div className="linkedCard cards" key={index}>
                <Hotels
                  key={index}
                  name={value.establishmentName}
                  price={value.price}
                />
              </div>
            );
          })
        ) : (
          <>
            <ReactLoading
              type={"spinningBubbles"}
              color={"#51a9b6a1"}
              height={100}
              width={100}
            />
          </>
        )}
      </div>
    </>
  );
}

export default Home;
