import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";

import establishments from "./../lib/establishments.json";

import Hotels from "./../components/hotels";

import search_icon from "./../svgs/icons/search_icon";

function Home() {
  useEffect(() => {}, []);

  let handleHotelSearch = (input) => {
    const hotelSearch = input.target.value;
  };

  return (
    <>
      <header className="container">
        <div className="header">
          <h1>BERGEN</h1>
          <p>
            Explore the city of Norway <br />
            surrounded by The Seven Mountains
          </p>
          <div className="searchBar">
            <input
              type="text"
              placeholder={`Search for a hotel`}
              onChange={handleHotelSearch}
            />
            <button className="submitHotelSearch">
              <img src={search_icon} alt="search" />
            </button>
          </div>
        </div>
      </header>
      <div className="grid">
        {establishments ? (
          establishments.map((value, index) => {
            return (
              <div key={index}>
                <Hotels
                  key={index}
                  name={value.establishmentName}
                  price={value.price}
                />
              </div>
            );
          })
        ) : (
          <>
            <ReactLoading
              type={"spinningBubbles"}
              color={"#51a9b6a1"}
              height={100}
              width={100}
            />
          </>
        )}
      </div>
    </>
  );
}

export default Home;
