import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";
import Collapsible from "react-collapsible";

import establishments from "./../lib/establishments.json";

import Hotels from "./../components/hotels";

import search_icon from "./../svgs/icons/search_icon.svg";

function Home() {
  useEffect(() => {}, []);

  let handleHotelSearch = (input) => {
    const hotelSearch = input.target.value;
  };

  let handleHotelFilter;

  return (
    <>
      <header className="container">
        <div className="header">
          <h1>BERGEN</h1>
          <p>
            Explore the city of Norway <br />
            surrounded by The Seven Mountains
          </p>
          <div className="searchBar">
            <input
              type="text"
              placeholder={`Search for a hotel`}
              onChange={handleHotelFilter}
            />
            <button onClick={handleHotelSearch}>
              <img src={search_icon} alt="search" />
            </button>
          </div>
        </div>
      </header>

      <div className="navCollapsible">
        <Collapsible trigger="Browse sets" open={true}>
          <NavLink to="/">
            <div className="setList">
              {setNames ? (
                setNames.map((value, index) => {
                  return (
                    <div
                      key={index}
                      className={
                        value.released_at <= today
                          ? "listItem"
                          : "listItem__hide"
                      }
                      title={value.name}
                    >
                      <img src={value.icon_svg_uri} alt={value.code} />
                      <button
                        key={index}
                        onClick={() => {
                          const queryCode = value.code;
                          console.log(value.code);
                          dispatch(CardAction(`set:${queryCode}`, ""));
                        }}
                      >
                        <p>{value.name}</p>
                      </button>
                    </div>
                  );
                })
              ) : (
                <li>
                  <ReactLoading
                    type={"spinningBubbles"}
                    color={"#5494bea1"}
                    height={30}
                    width={30}
                  />
                </li>
              )}
            </div>
          </NavLink>
        </Collapsible>
      </div>

      <div className="grid">
        {establishments ? (
          establishments.map((value, index) => {
            return (
              <div className="card" key={index}>
                <Hotels
                  key={index}
                  name={value.establishmentName}
                  price={value.price}
                />
              </div>
            );
          })
        ) : (
          <>
            <ReactLoading
              type={"spinningBubbles"}
              color={"#51a9b6a1"}
              height={100}
              width={100}
            />
          </>
        )}
      </div>
    </>
  );
}

export default Home;
