import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";

import establishments from "./../components/establishments.json";

import hotels from "./../components/hotels";

function Home() {
  const [debounce, setDebounce] = useState();
  useEffect(() => {}, []);

  let handleHotelSearch = (input) => {
    if (debounce) {
      clearTimeout(debounce);
    }
    const hotelSearch = input.target.value;
    setDebounce(
      setTimeout(() => establishments),
      300
    );
  };

  return (
    <>
      <div className="wideCardContainer">
        <div className="searchField">
          <div>
            <input
              type="text"
              placeholder={`Search in all cards`}
              autoFocus
              onChange={handleHotelSearch}
            />
          </div>
        </div>
      </div>
      <div className="smallCardContainer overview">
        {hotels ? (
          hotels.map((value, index) => {
            //temporary solution until pagination fix
            return (
              <div className="linkedCard cards" key={index}>
                <Cards
                  key={index}
                  id={value.oracle_id}
                  set={value.set}
                  collectorNumber={value.collector_number}
                  name={value.name}
                  image={
                    value.image_uris
                      ? value.image_uris.border_crop
                      : "https://img.scryfall.com/cards/normal/front/0/5/05e5bb13-d2b1-41fa-9cdf-70efa40455d4.jpg?1579777567"
                  }
                />
              </div>
            );
          })
        ) : (
          <>
            <ReactLoading
              type={"spinningBubbles"}
              color={"#51a9b6a1"}
              height={100}
              width={100}
            />
          </>
        )}
      </div>
    </>
  );
}

export default Home;
