import React, { useEffect, useState } from "react";
import ReactLoading from "react-loading";

import establishments from "./../components/establishments.json";
import { CardAction } from "./../store/actions/cardAction";

import Cards from "./../components/cards";

function Home() {
  const [debounce, setDebounce] = useState();
  useEffect(() => {}, []);

  let handleCharSearch = (input) => {
    if (debounce) {
      clearTimeout(debounce);
    }
    const hotelSearch = input.target.value;
    setDebounce(
      setTimeout(() => establishments),
      300
    );
  };

  return (
    <>
      {establishments ? (
        establishments.map((value, index) => {
          return (
            <div key={index}>
              <p>{value.name}</p>
            </div>
          );
        })
      ) : (
        <>
          <ReactLoading
            type={"spinningBubbles"}
            color={"#51a9b6a1"}
            height={100}
            width={100}
          />
        </>
      )}
    </>
  );
}

export default Home;
